import '../stylesheets/App.css';
import React from 'react';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      name: '',
      description: '',
      language: '',
      sel: '',
    };
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(ev) {
    console.log(ev.target);
    this.setState({
      name: ev.target.value,
    });
  }

  render() {
    return (
      <div className="card">
        <h1 className="headerTitle">Formulario para pelis</h1>
        <form action="">
          <label htmlFor="name">
            Nombre:
            <input type="text" value={this.state.name} onChange={this.handleChange} />{' '}
          </label>
          <textarea value="" description={this.state.description} onChange={this.handleChange}>
            Escribe aquí tu descripción:
          </textarea>

          <select className="container__select" name="sel" id="sel" value={this.state.sel} onChange={this.handleChange}>
            <option value="Español">Español</option>
            <option value="Inglés">Inglés</option>
            <option value="Portugués">Portugués</option>
          </select>
          <input type="submit" value="Enviar" />
        </form>
      </div>
    );
  }
}

export default App;
